import * as canvasWalletInterface from '@dscvr-one/canvas-wallet-interface';
export { canvasWalletInterface as CanvasInterface };
import * as _wallet_standard_core from '@wallet-standard/core';
import { Wallet, WalletAccount, StandardEventsOnMethod, StandardEventsNames, StandardEventsListeners, StandardConnectFeature, StandardDisconnectFeature, StandardEventsFeature } from '@wallet-standard/core';
import { CanvasClient } from '@dscvr-one/canvas-client-sdk';
import { SolanaSignAndSendTransactionFeature, SolanaSignTransactionFeature, SolanaSignMessageFeature } from '@solana/wallet-standard';
import { WalletName } from '@solana/wallet-adapter-base';

declare abstract class AbstractWallet implements Wallet {
    #private;
    protected _accounts: WalletAccount[];
    get version(): "1.0.0";
    abstract get name(): Wallet['name'];
    abstract get icon(): Wallet['icon'];
    abstract get chains(): Wallet['chains'];
    abstract get features(): Wallet['features'];
    get accounts(): WalletAccount[];
    constructor();
    protected _on: StandardEventsOnMethod;
    protected _emit<E extends StandardEventsNames>(event: E, ...args: Parameters<StandardEventsListeners[E]>): void;
    protected _off<E extends StandardEventsNames>(event: E, listener: StandardEventsListeners[E]): void;
}

declare const CANVAS_WALLET_NAME: WalletName;
declare class CanvasWallet extends AbstractWallet implements Wallet {
    #private;
    get name(): WalletName;
    get icon(): "data:image/svg+xml;base64,";
    get chains(): ("solana:mainnet" | "solana:devnet" | "solana:testnet" | "solana:localnet")[];
    get features(): StandardConnectFeature & StandardDisconnectFeature & StandardEventsFeature & SolanaSignAndSendTransactionFeature & SolanaSignTransactionFeature & SolanaSignMessageFeature;
    private options;
    constructor(canvasClient: CanvasClient);
    setCanvasClient(canvasClient: CanvasClient): void;
    private validateResponse;
    private sendMessage;
}

declare const registerCanvasWallet: (canvasClient: CanvasClient) => _wallet_standard_core.Wallet | CanvasWallet;

export { CANVAS_WALLET_NAME, CanvasWallet, registerCanvasWallet };
